
# Popcorn 2.3
# https://github.com/InitializeSahib/Popcorn


class PopConv:

    def kilo_to_pound(self, kilo):
        return kilo * 2.3046

    def pound_to_kilo(self, pound):
        return pound / 2.3046

    def yard_to_foot(self, yard):
        return yard * 3

    def foot_to_yard(self, foot):
        return foot / 3

    def pound_to_stone(self, pound):
        return pound / 14

    def stone_to_pound(self, stone):
        return stone * 14